INSERT INTO book (title, description, author, isbn, printYear, readAlready) VALUES
  ('Стигмалион', '"Меня зовут Долорес Макбрайд, и я с рождения страдаю от очень редкой формы аллергии: прикосновения к другим людям вызывают у меня сильнейшие ожоги. Я не могу поцеловать парня, обнять родителей, выйти из дому, не надев перчатки. Я неприкасаемая. Я словно живу в заколдованном замке, который держит меня в плену и наказывает ожогами и шрамами за каждую попытку «побега». Даже придумала имя для своей тюрьмы: Стигмалион. Меня уже не приводит в отчаяние мысль, что я всю жизнь буду пленницей своего диагноза — и пленницей умру. Я не тешу себя мечтами, что от моей болезни изобретут лекарство, и не рассчитываю, что встречу человека, не оставляющего на мне ожогов… Но до чего же это живучее чувство — надежда. А вдруг я все-таки совершу побег из Стигмалиона? Вдруг и я смогу однажды познать все это: прикосновения, объятия, поцелуи, безумство, свободу, любовь?.."', 'Старк К.', '9785171070656', 2018, FALSE),
  ('Срывая маски. Фантастический роман', 'Максим Рудов под маской Сакурая Синдзи все глубже погружается в мир аристократов, мир интриг, соперничества и союзов. Строятся планы на будущее и тут же проверяются на прочность под ударами судьбы. А удары приходят с разных, порой самых неожиданных сторон, ведь не всегда это враги. Кто-то действует и из лучших побуждений, по-своему понимая, как "лучше". И чтобы избежать краха, необходимы сила, хитрость и ум. И много наглости, чтобы решиться на судьбоносные шаги. Ведь ведьмак класса "абсолют" - это не только сила, но также лисья хитрость, острый ум и море наглости...', 'Метельский Н.', '9785992226058', 2018, FALSE ),
  ('Веном. Смертоносный защитник', 'Когда Эдди Брок отправляется на запад, врагов, которые так и рвутся испытать его решимость, хватает - и Присяжные в их числе! К тому же Человек-Паук встречает отца Венома! А сам Веном порождает целую орду новых зловещих симбиотов! Классический Веном в первой - и смертоноснейшей - сольной серии!', 'Микелайни Д., Багли М., Лим Р.', '9785918782378', 2017, FALSE ),
  ('Ведьмочка и большие мухоморы', 'Непреложное правило гласит: светлая ведьма никогда не должна использовать свою силу в корыстных целях. В день, когда Аксинья нарушила это правило, она узнала, что скоро ей грозит страшная гибель, вылетела из Ведовской школы, лишилась жилья, получила беспокойное наследство и встретила любовь. .А что из этого вышло, узнаем дальше…', 'Полянская К.', '9785171066727', 2018, FALSE ),
  ('#Зановородиться. Невероятная история любви', 'Саша Петров — один из самых востребованных молодых актеров страны, за плечами которого главные роли в блокбастере «Притяжение», киносериалах «Гоголь», «Полицейский с Рублевки», «Метод» и других масштабных проектах российского кино. .Еще одну грань своего таланта Петров раскрыл, создав полностью самостоятельный проект — драматическое шоу #ЗАНОВОРОДИТЬСЯ, в котором он и сценарист, и режиссер, и единственный актер. .Это невероятная история запретной любви между мужчиной и женщиной, которая буквально сводит обоих с ума, а герой Петрова отчаян до безумия. .Свидетелями душераздирающего шоу, порой доводящего до катарсиса, стали зрители клубных площадок России, а также искушенная театральная публика... Теперь пришло время изложить эту историю на бумаге. .В своей первой книге Саша представит собственные стихи, а также поделится сокровенными мыслями о бесконечно вдохновляющем чувстве — о любви... и обязательно расскажет о том, как он написал сценарий #ЗАНОВОРОДИТЬСЯ и кто его вдохновил. .', 'Петров А.', '9785040909865', 2018, FALSE ),
  ('Мод. Откровенная история одной семьи', 'Нескладная девочка, нелюбимая дочь, покорная жена... Мод обожала первого мужа и презирала второго. Была прекрасной матерью, но только для троих из пяти своих детей. Обыкновенная женщина с необыкновенной судьбой. История большой любви и величайшей трагедии. Автор этой книги, Донна Мабри, зарабатывала на жизнь шитьем и продажей косметики. На пенсии Донна решила написать историю жизни своей бабушки Мод. Не получив поддержки от издательств, Донна самостоятельно опубликовала книгу в интернет-магазине. Спустя всего несколько недель "Мод" обогнала по продажам раскрученные блокбастеры, продалась тиражом более 1 000 000 экземпляров и стала самой обсуждаемой книгой в книжных клубах Америки.', 'Мабри Д.', '9785040887316', 2018, FALSE ),
  ('Черная Пантера', 'После событий, описанных в фильме Marvel "Первый Мститель: Противостояние", король Т''Чалла возвращается домой, в Ваканду – затерянное в джунглях, но весьма технологически развитое африканское государство. Юноша готовится стать новым лидером своего народа, однако обнаруживает, что за трон еще придется побороться с некоторыми из соотечественников. .Когда двое врагов Т''Чаллы объединяются, чтобы уничтожить Ваканду, герой племени, известный как "Черная Пантера", с союзниками в лице агента ЦРУ Эверетта К. Росса и членов элитной гвардии страны вынужден принимать решительные меры, чтобы не допустить вовлечения Ваканды в мировую войну.', 'МакКанн Дж., Белинг С.', '9785171064150', 2018, TRUE ),
  ('Щ.И.Т. Том 2. Мужчина по имени С.М.Е.Р.Т.Ь.', 'Домашняя еда всегда вкуснее и полезнее любой покупной – это знает каждый. Но ведь такие продукты, как колбасы, вяленое мясо, сыры или хлеб, наверняка слишком сложны для приготовления! Вы до сих пор так думаете? И совершенно зря! Анна Китаева, автор бестселлеров "Я и моя мультиварка" и "Я и моя хлебопечка", докажет, что все намного проще, чем кажется. Любой хозяйке под силу заменить покупную еду домашней, от копченых колбас до соусов! Главное – знание правильной технологии и желание сохранить здоровье и прекрасное самочувствие на долгие годы.', 'Уэйд М., Смолвуд Г.', '9785040896752', 2018, FALSE ),
  ('Русская «Синева». Война невидимок', 'Что может случиться, если Президентом США станет некомпетентная скандальная женщина-политик? Что может произойти из-за прямого военного нападения на Россию самонадеянной Америки? Поможет ли пентагоновским воякам новое суперсекретное оружие?.На пути адептов нового мирового порядка встают «самые верные союзники» России – ее армия, флот и военно-космические силы!', 'Хабибулин Ю.', '9785040912568', 2018, FALSE ),
  ('Вращая колесо Сансары', 'Егор Мальцев успевает не только играть в футбол и заниматься музыкой, но и решать проблемы на личном фронте. Однако всё же главная его цель - выступить на чемпионате мира по футболу 1966 года. Том самом, на котором сборная СССР сумела занять четвёртое место, самое высокое в своей истории. Но сможет ли наша команда, ведомая Егором Мальцевым, на этот раз удивить весь футбольный мир? Во всяком случае, герой приложит к этому максимум усилий.', 'Марченко Г.', '9785227080141', 2018, FALSE ),
  ('Весы', 'Пьесы, вошедшие в этот сборник, как и все произведения Гришковца, имеют отношение к современнику, к человеку переживающему, думающему, внимательному. Здесь есть монологи, которые Гришковец исполняет на сцене сам, и пьесы, написанные для постановок в театрах в привычном понимании этого слова. Есть хорошие люди в непростых обстоятельствах, есть тревоги, волнения, радость, забота, трудный выбор... и обязательно надежда.', 'Гришковец Е.', '9785040893652', 2018, FALSE ),
  ('На разломе двух времен. 80-е', 'Сергей Васильев — первый инвестор и председатель совета директоров «Рамблера» в 1999–2001 годах, инвестбанкир и председатель совета директоров инвестиционной группы «Русские Фонды». Но это сейчас. А раньше были советское детство, пионерлагеря, школа, техникум со стенгазетами и комсомольскими собраниями, учеба в МФТИ, «картошка», стройотряды, студенческое общежитие, работа инженером и первые поиски своего пути в кооператорстве и банковском бизнесе. Вся эта череда событий происходила на фоне исторических перемен, которые, набирая обороты, неумолимо вели к переломному моменту в жизни огромной страны — развалу СССР, разделившему нашу жизнь на до и после... Книга — сборник биографических рассказов, личных писем и фотографий автора на рубеже 1980–1990-х годов. Это частные истории из недавнего прошлого нашей страны, такие знакомые для каждого, кто застал ту эпоху, и в то же время такие личные.', 'Васильев С.', '9785961466874', 2018, TRUE ),
  ('Навсе...где?', 'Шестнадцатилетняя Никси живет со своим отцом, которого все называют капитаном Слэйтом. Он – человек неординарный. Во-первых, он весь – буквально с головы до ног – покрыт татуировками. Во-вторых, он – заядлый любитель путешествовать, и у него даже есть свой собственный корабль-парусник, "Искушение". И в-третьих, он не обычный мореплаватель, а настоящий путешественник во времени… Индия 18 века, Нью-Йорк 21-го, Гавайи 19-го – ему не составляет труда оказаться где угодно, главное, чтобы у него была карта, желательно той эпохи и нарисованная от руки. Вот уже 16 лет Никси странствует вместе со своим отцом. И всё это время Слэйт одержим одной-единственной целью – вернуться в Гонолулу 1868 года. Именно там он познакомился с матерью Никси и только там был по-настоящему счастлив – пока она не умерла при родах… Раз за разом, карта за картой Слэйт пытается доплыть до заветного острова, но всё безуспешно. В чем же причина этих неудач? Почему Провидение не дает ему воссоединиться с любовью всей его жизни? И какую цену придется заплатить ему за исполнение своей заветной мечты?..', 'Хэйлиг Х.', '9785171000486', 2018, FALSE ),
  ('Шоу не должно продолжаться', 'Зверски убит популярный телеведущий Евгений Березин. В последнее время он активно освещал проблему терроризма и даже приглашал на прямой эфир бывших боевиков. Раскрыть преступление поручено полковнику МВД Гурову. Знакомясь с обстоятельствами дела, сыщик пришел к выводу, что желающих избавиться от Березина было немало. Здесь и конкурент по телеканалу, и бывшая жена, недавно ушедшая к другому, и герои его программ… Все эти версии оставались рабочими до тех пор, пока не произошло страшное событие, которое кардинально изменило направление следствия.', 'Леонов Н., Макеев А.', '9785040902446', 2018, FALSE ),
  ('Легенда о Темном Эльфе. Книга III: Темное наследие. Беззвездная ночь. Нашествие тьмы. Путь к рассвету', 'Казалось, время скитаний и опасных авантюр осталось позади. Темный эльф Дзирт ДоУрден вместе с друзьями обосновался в отвоеванном у темных сил легендарном Мифрил Халле и собрался было вести спокойную и размеренную жизнь. Но в прошлом благородного дроу осталось много могущественных врагов, поклявшихся отомстить мятежному принцу. И Дзирт опять вынужден спуститься в Подземье, мир вечного мрака и ужаса, куда тянутся нити заговора, грозящего гибелью его друзьям. Темному эльфу вновь предстоит принять вызов и сразиться с монстрами настолько страшными, что им нет места под солнцем. Чтобы навсегда ушло из его жизни требующее все новых и новых жертв темное наследие.', 'Сальваторе Р.', '9785918782101', 2017, FALSE ),
  ('Волчья луна', 'Продолжение легендарного приключенческого цикла от автора бестселлеров New York Times. .В недобрые времена историк Джереми Логан приехал в пансионат на территории знаменитого природного парка, чтобы завершить монографию о средневековой ереси. Странные убийства совершаются в его дебрях, и абсолютно неясно, кто мог напасть на жертв и так их растерзать: то ли медведь, то ли волки, то ли одержимый извращенной жестокостью человек. Ни одна версия не кажется подходящей. И только участвующий в расследовании лесной рейнджер Рэндалл Джессап знает, что его старый университетский друг Джереми не только историк, но и энигматолог, специалист по разгадыванию необъяснимых загадок в любых, даже самых трудных условиях. Его помощь в этом деле - единственная возможность выяснить правду… . .', 'Чайлд Л.', '9785040903870', 2018, TRUE ),
  ('«Я слышал, ты красишь дома». Исповедь киллера мафии «Ирландца»', '"Я слышал, ты красишь дома" - на языке мафии это выражение означает "Я слышал, ты умеешь убивать людей", а под "краской" подразумевается кровь. .Это тот редкий случай, когда боссы мафии признали книгу о себе правдивой - штатный киллер одной из "семей" Фрэнк "Ирландец" Ширан рассказал о своей жизни перед самой смертью. Эти истории, затаив дыхание, слушали опытные прокуроры и агенты ФБР. Впервые преступник такого уровня нарушил омерту - закон молчания. .Хулиганская юность в годы Великой депрессии, первый запах крови во Вторую мировую, случайное попадание в закрытый мир итало-американской мафии, выход из которого дороже входа - когда тебе нужно выбрать между своей жизнью и жизнью лучшего друга. .И все это в легендарную эпоху 50-х в США, когда коррумпированный директор профсоюза Джимми Хоффа владел миллиардами и открыто соперничал с братьями Кеннеди, боровшимися против мафии. Почему был убит президент Кеннеди и почему от этого проиграли все? Кто был прототипом Крестного отца в знаменитом фильме? И что заставило заматерелого убийцу Фрэнка "Ирландца" Ширана прийти к искупающей исповеди? .', 'Брандт Ч.', '9785040910632', 2018, FALSE ),
  ('Британский вояж', 'Машина времени, открывшая дорогу из будущего в прошлое, успешно работает. Теперь уже не кучка энтузиастов, а вполне серьезные правительственные структуры РФ вышли на прямую связь с императором Николаем I. Сотрудники спецслужб России помогают жандармам бороться с вражеской агентурой, а глава III отделения граф Бенкендорф отправляется в командировку в будущее, дабы там немного подлечиться и встретиться кое с кем из руководства РФ. А в Лондон с деликатной миссией отправилась группа силовиков для поимки Дэвида Уркварта - самого главного ненавистника России. Именно он направлял на Кавказ в помощь мятежным горцам оружие и деньги, добровольцев и военных советников. Нужно во что бы то ни стало отловить его и доставить в Петербург.', 'Михайловский А., Харников А.', '9785171065973', 2018, FALSE ),
  ('Война Калибана', 'Итак, мы не одиноки. На Ганимеде, житнице внешних планет, марсианка-спецназовец становится свидетелем гибели своего взвода, истребленного чудовищным суперсолдатом. На Земле высокопоставленный политик изо всех сил пытается предотвратить межпланетную войну. А на Венере обосновалась чуждая протомолекула, производя таинственные трансформации и угрожая распространиться по Солнечной системе. В бескрайнем космосе Джеймс Холден и экипаж "Росинанта" заняты поддержанием мира от имени Альянса Внешних Планет. Они соглашаются помочь в поисках пропавшего на Ганимеде ребенка и внезапно обнаруживают, что будущее человечества зависит от того, сможет ли один корабль предотвратить межпланетное вторжение, которое, возможно, уже началось...', 'Кори Дж.', '9785918782286', 2018, FALSE ),
  ('Пособие для ленивого студента. Роман', 'Что делать, если твой напарник по учебе – полный разгильдяй, а ты - лучшая студентка на курсе? Естественно, попытаться личным примером доказать, что знание – сила! Правда, вот беда, мы постоянно попадаем в какие-то неприятности. Эх, и свалилось же такое "счастье" мне на голову!..', 'Малиновская Е.', '9785992226003', 2018, FALSE ),
  ('Сладкое зло', 'Жизнь молодых людей полна соблазнов - такова реальность. Для сыновей и дочерей падших ангелов всё еще сложнее, ведь они имеют дело с людскими пороками. Но шестнадцатилетняя Анна Уитт, девушка с душой нежной и горячей, как солнце в ее родной Атланте, всегда выбирает светлую сторону. Обладая сверхъестественной способностью чувствовать эмоции других людей, она умеет противиться силам зла. Однако сможет ли Анна устоять перед искушением после встречи с Каиданом, покорившим ее сердце?', 'Хиггинс В.', '9785179832911', 2018, FALSE ),
  ('Блистательный Двор', 'Семнадцатилетняя сирота графиня Элизабет принадлежит к древнему, но обедневшему роду и, конечно, не хочет выходить замуж за нудного богатого кузена. И когда Элизабет выпадает шанс изменить жизнь, она хватается за него и меняется местами с горничной Адой. Ведь простушке Аде представилась возможность попасть в Блистательный Двор, где сбываются мечты неимущих красоток. Юных девиц вербуют, дабы отправить в загадочную заморскую Адорию, где они смогут стать женами эмигрантов-нуворишей.
